SELECT C.CODE, C.NAME, C.TYPE, TEACHER.NAME, CLASSROOM.NAME, C.SEMESTER, C.CREDIT FROM H668139.COURSE C INNER JOIN TEACHER ON C.TEACHER_ID=TEACHER.ID INNER JOIN CLASSROOM ON C.CLASSROOM_ID = CLASSROOM.ID INNER JOIN HALLGATO H ON H.EHA=? AND (H.EHA, C.CODE) NOT IN (SELECT RC.HALLGATO_EHA, RC.COURSE_CODE FROM REGISTEREDCOURSE RC)