SELECT DISTINCT VIP.DATUM, C1.CODE, C1.NAME, CR.NAME, VIP.JELENTKEZETT, VIP.MAX_JELENTKEZO FROM VIZSGAIDOPONT VIP INNER JOIN COURSE C1 ON C1.CODE=VIP.KURZUS_KOD INNER JOIN CLASSROOM CR ON CR.ID=VIP.TEREM_KOD WHERE VIP.KURZUS_KOD IN (SELECT COURSE_CODE FROM REGISTEREDCOURSE WHERE HALLGATO_EHA=?) AND VIP.KURZUS_KOD NOT IN (SELECT VI_KURZUS_KOD FROM VIZSGA)