SELECT DISTINCT V.VI_DATUM, C.CODE, C.NAME, VI.JELENTKEZETT, VI.MAX_JELENTKEZO, V.JEGY FROM VIZSGA V INNER JOIN COURSE C ON C.CODE=V.VI_KURZUS_KOD INNER JOIN VIZSGAIDOPONT VI ON VI.DATUM=V.VI_DATUM AND V.VI_KURZUS_KOD=VI.KURZUS_KOD INNER JOIN REGISTEREDCOURSE RC ON RC.COURSE_CODE=V.VI_KURZUS_KOD WHERE V.HALLGATO_EHA=? ORDER BY 1,3 ASC